package erc20

import (
	"os"

	"github.com/ethereum/go-ethereum/accounts/abi"
)

const (
	ERC20DeployCode = ``
)

var erc20abi abi.ABI

func init() {
	var err error
	// Read the ABI from file
	// Try multiple paths to find erc20abi.json
	var abiFile *os.File
	found := false
	paths := []string{
		"erc20abi.json",
		"./cmd/benchmark/erc20/erc20abi.json",
		"./erc20/erc20abi.json",
	}
	for _, path := range paths {
		abiFile, err = os.Open(path)
		if err == nil {
			found = true
			break
		}
	}
	if !found {
		panic("could not find erc20abi.json in any of the search paths")
	}
	defer abiFile.Close()

	erc20abi, err = abi.JSON(abiFile)
	if err != nil {
		panic(err)
	}
}
